import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [enteredFilter, setEnteredFilter] = useState("All");

  const filterHandler = (selectedFilter) => {
    setEnteredFilter(selectedFilter);
  };

  const filteredExpenses = props.items.filter((expense) => {
    if(exteredFilter === "All") {
      return 
    } 
    return expense.date.getFullYear().toString() === enteredFilter;
  });

  return (
    <Card className="expenses">
      <ExpensesFilter selected={enteredFilter} onChangeFilter={filterHandler} />
      
      {filteredExpenses.length === 0 && (
        <p className="parag">No expenses found.</p>
      )}

      {filteredExpenses.length > 0 &&
        filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
    </Card>
  );
};

export default Expenses;
