import React, { useState } from 'react'; 
import "./Expenses.css";
import ExpenseItem from './ExpenseItem';
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {



  const [enteredFilter, setEnteredFilter] = useState('2021');
  
  const formChangeHandler = (selectedFilter) => {
    setEnteredFilter(selectedFilter);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter selected = {enteredFilter} onChangeFilter = {formChangeHandler}></ExpensesFilter>
      {props.items.map(expense => <ExpenseItem title={expense.title} amount={expense.amount} date={expense.date}/>)}
    </Card>
  );
}

export default Expenses;
